function initMap(){map=new google.maps.Map(document.getElementById("map"),{center:londonLatLang,zoom:13,scrollwheel:!1}),google.maps.event.addDomListener(window,"load",function(){var o=new google.maps.Marker({animation:google.maps.Animation.DROP,position:{lat:51.507351,lng:-.127758},map:map,id:1,icon:icon,title:"Marker1"});allMarkers.push(o),o.addListener("mouseover",function(){console.log("marker1 mouseover"),$("#tile1").toggleClass("highlight")}),o.addListener("mouseout",function(){console.log("marker1 mouseout"),$("#tile1").toggleClass("highlight")});var e=new google.maps.Marker({animation:google.maps.Animation.DROP,position:{lat:51.504787,lng:-.113469},map:map,id:2,icon:icon,title:"Marker2"});allMarkers.push(e),e.addListener("mouseover",function(){console.log("marker2 mouseover"),$("#tile2").toggleClass("highlight")}),e.addListener("mouseout",function(){console.log("marker2 mouseout"),$("#tile2").toggleClass("highlight")});var a=new google.maps.Marker({animation:google.maps.Animation.DROP,position:{lat:51.524112,lng:-.118147},map:map,id:3,icon:icon,title:"Marker3"});allMarkers.push(a),a.addListener("mouseover",function(){console.log("marker3 mouseover"),$("#tile3").toggleClass("highlight")}),a.addListener("mouseout",function(){console.log("marker3 mouseout"),$("#tile3").toggleClass("highlight")});var l=new google.maps.Marker({animation:google.maps.Animation.DROP,position:{lat:51.508051,lng:-.168641},map:map,id:4,icon:icon,title:"Marker4"});allMarkers.push(l),l.addListener("mouseover",function(){console.log("marker4 mouseover"),$("#tile4").toggleClass("highlight")}),l.addListener("mouseout",function(){console.log("marker4 mouseout"),$("#tile4").toggleClass("highlight")})})}function hover(o){for(var e=0;e<allMarkers.length;e++)if(o===allMarkers[e].id){allMarkers[e].setIcon(iconLight);break}}function out(o){for(var e=0;e<allMarkers.length;e++)if(o===allMarkers[e].id){allMarkers[e].setIcon(icon);break}}var londonLatLang={lat:51.507351,lng:-.127758},map,icon="img/logo.png",iconLight="img/pin-green.png",allMarkers=[];